import React, { useState } from "react";
import "./App.css";
import Header from "./components/header";
import { useRef } from 'react';

const images = require.context('./assets', true, /\.png$/);
const imagePath = (name) => images(name, true);


function App() {
  // const videoRef = useRef(null);

  // const handleMouseOver = () => {
  //   videoRef.current.style.display = 'block';
  //   videoRef.current.play();
  // };

  // const handleMouseOut = () => {
  //   videoRef.current.pause();
  //   videoRef.current.style.display = 'none';

  // };
  const [isShown, setIsShown] = useState(false);

  const src = "DUBIDUBIDU.mp4";

  return (
    <div className="main">
      <Header />
      <div className="body">
        <div className="left">
          <section className="about">
            <article>
              <h2>About Me</h2>
              <p>
              Je suis actuellement étudiant en première année à Epitech Montpellier, mes compétences actuelles se reposent sur   le C, Python et le développement web.
              Au cours de mon parcours académique, j'ai eu l'opportunité de faire un stage au près d'un administrateur web au sein d'une entreprise de communication. Cette expérience m'a permis de plonger dans le monde professionnel et de découvrir le web.
              Ce que j'apprécie en programmation, c'est la possibilité de créer des solutions innovantes et fonctionnelles qui résolvent des problèmes concrets. Mon objectif est de continuer à apprendre et à évoluer, en exploitant mes compétences techniques pour contribuer de manière significative à des projets stimulants.
              </p>
            </article>
          </section>
          <section className="projects">
            <h2>Mes projets</h2>
            <article>
              <h3>Bataille navale</h3>
              {/* {isShown && <video src={src} autoPlay={true} loop={true} controls={true} />} */}  
              <img className="feature-img" src={imagePath('./navy.png')} alt="slug" style={{width: "60%", height: "60%"}}
                onMouseOver={setIsShown(true)}
                onMouseOut={setIsShown(false)}/>
              {/* <video
                className="feature-img"
                id="myVideo" 
                width="320" 
                height="240"
                style={{display: 'none'}}
                onMouseOver={setIsShown(true)} 
                onMouseOut={setIsShown(false)} 
                ref={videoRef}
              >
                <source src={src} type="video/mp4" />
                  Your browser does not support this video format.
              </video> */}
              <div className="project-info">
                <h3>Infos :</h3>
              <p>
                Jeu de bataille navale 2 joueurs en local réalisé en C.
              </p>
              </div>
              <br></br>
              <h3>Duck hunt remaster</h3>
              <img className="feature-img" src={imagePath('./hunter.png')} alt="slug" style={{width: "60%", height: "60%"}}/>
              <div className="project-info">
                <h3>Infos :</h3>  
              <p>
                Duck hunt remaster est un jeu de tir sur cible réalisé en C qui vise a recréer le jeu original modifié.
              </p>
              </div>
            </article>
          </section>
          </div>
      </div>
    </div>
  );
}

export default App;
